import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Cart, CartProduct } from '../model/cart';
import { Product } from '../model/product';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private cartUrl = 'https://jsonplaceholder.typicode.com/cart';
  private carts: Cart[] = [];

  constructor(private router: Router, private http: HttpClient) { }

  saveCartToDB(cart: Cart): void {
    const userEmail = localStorage.getItem('user');
    localStorage.setItem(`cart_${userEmail}`, JSON.stringify(cart));
  }


  getUserCart(): Cart {
    const userEmail = localStorage.getItem('user');
    if (!userEmail) {
      // userEmail is not stored in local storage, so return an empty cart
      return new Cart();
    }

    const cart = this.carts.find(c => c.user === userEmail);
    if (!cart) {
      const newCart = new Cart();
      newCart.user = userEmail;
      this.carts.push(newCart);
      return newCart;
    }

    return cart;
  }

  addToCart(product: CartProduct): void {
    const cart = this.getUserCart();
    let existingProduct = cart.products.find(p => p.product.title === product.product.title);
    if (existingProduct) {
        existingProduct.qty++;
        cart.totalPrice += product.product.price;
    } else {
        cart.products.push(product);
        cart.totalPrice += product.product.price;
    }

    this.saveCartToDB(cart);
  }

  removeFromCart(product: CartProduct): void {
    const cart = this.getUserCart();
    const index = cart.products.indexOf(product);
    if (index > -1) {
      cart.products.splice(index, 1);
      cart.totalPrice -= product.product.price * product.qty;
    }

    this.saveCartToDB(cart);
  }

  quantityUp(product: CartProduct): void {
    const cart = this.getUserCart();
    const index = cart.products.indexOf(product);
    if (index > -1) {
      cart.products[index].qty += 1;
      cart.totalPrice += product.product.price;
    }

    this.saveCartToDB(cart);
  }

  quantityDown(product: CartProduct): void {
    const cart = this.getUserCart();
    const index = cart.products.indexOf(product);
    if (index > -1) {
      cart.products[index].qty -= 1;
      cart.totalPrice -= product.product.price;
    }

    this.saveCartToDB(cart);
  }

  getItems(): CartProduct[] {
    const cart = this.getUserCart();
    return cart.products;
  }

  getTotalPrice(): number {
    const cart = this.getUserCart();
    return cart.totalPrice;
  }
  payment() {
    this.carts.paid = true;
  }


}
