import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { User } from '../model/user';
import { UsersService } from '../services/users.service';
import { CartService, CartProduct } from '../services/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  users: User[] = [];
  loggedIn: any;
  public products: any[] = [];
  items: CartProduct[] = [];
  totalPrice: number = 0;

  constructor(private cartService: CartService, private router: Router, private userService: UsersService, private activeRoute: ActivatedRoute) { }

  ngOnInit(): void {
    const email = this.activeRoute.snapshot.params['email'];
    this.items = this.cartService.getUserCart();
    this.totalPrice = this.cartService.calculateTotal();
    // this.users = this.userService.getUsers();
    this.loggedIn = this.users.find(user => user.email === email);
  }

  removeItem(item: CartProduct) {
    this.cartService.removeFromCart(item);
    this.items = this.cartService.getUserCart();
    this.totalPrice = this.cartService.calculateTotal();
  }
  clearCart() {
    this.cartService.clearCart();
    this.items = this.cartService.getUserCart();
    this.totalPrice = this.cartService.calculateTotal();
  }
  deletefromcart(product: CartProduct) {
    this.cartService.removeFromCart(product);
    this.totalPrice = this.cartService.calculateTotal();
    this.items = this.cartService.getUserCart();
  }
  QuantityUp(product: CartProduct) {
    this.cartService.addToCart(product);
    this.totalPrice = this.cartService.calculateTotal();
    this.items = this.cartService.getUserCart();
  }
  QuantityDown(product: CartProduct) {
    if (product.qty === 1) {
      this.deletefromcart(product);
      return;
    }
    this.cartService.removeFromCart(product);
    this.totalPrice = this.cartService.calculateTotal();
    this.items = this.cartService.getUserCart();
  }
  checkOut() {
    // implement the payment function if needed
    this.clearCart();
  }
}
