import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UsersService } from '../users.service';

@Component({
  selector: 'app-navbar',
  template: `
    <ul>
      <li><a>TechWorld</a></li>
      <li><a routerLinkActive="active" routerLink="home">Home</a></li>
      <li><a routerLinkActive="active" routerLink="catalog">Catalog</a></li>
      <li><a routerLinkActive="active" routerLink="about">About Us</a></li>
      <li><a routerLinkActive="active" routerLink="cart">Cart</a></li>
      <li><a (click)="onLoginLogout()" routerLinkActive="active" routerLink="profile">{{ loginText }}</a></li>
    </ul>
  `,
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  loginText: string;

  constructor(private router: Router, private usersService: UsersService) { }

  ngOnInit(): void {
    this.updateLoginText();
  }

  onLoginLogout() {
    if (this.loginText === "Login") {
      this.router.navigate(['/login']);
    } else {
      this.usersService.logout();
      this.updateLoginText();
      this.router.navigate(['/home']);
    }
  }

  updateLoginText() {
    this.loginText = this.usersService.currentUser ? "Logout" : "Login";
  }
}
